import{_ as a,c as t,o as r,m as e,a as o}from"./chunks/framework.D5hbrQnr.js";const b=JSON.parse('{"title":"第十四章　居住权","description":"","frontmatter":{},"headers":[],"relativePath":"repo/law-library/001 民法典/02 第二编：物权/03 第三分编：用益物权/14 第十四章：居住权.md","filePath":"repo/law-library/001 民法典/02 第二编：物权/03 第三分编：用益物权/14 第十四章：居住权.md"}'),s={name:"repo/law-library/001 民法典/02 第二编：物权/03 第三分编：用益物权/14 第十四章：居住权.md"},_=e("h1",{id:"第十四章-居住权",tabindex:"-1"},[o("第十四章　居住权 "),e("a",{class:"header-anchor",href:"#第十四章-居住权","aria-label":'Permalink to "第十四章　居住权"'},"​")],-1),n=e("p",null,"第三百六十六条　居住权人有权按照合同约定，对他人的住宅享有占有、使用的用益物权，以满足生活居住的需要。 第三百六十七条　设立居住权，当事人应当采用书面形式订立居住权合同。 居住权合同一般包括下列条款： （一）当事人的姓名或者名称和住所； （二）住宅的位置； （三）居住的条件和要求； （四）居住权期限； （五）解决争议的方法。 第三百六十八条　居住权无偿设立，但是当事人另有约定的除外。设立居住权的，应当向登记机构申请居住权登记。居住权自登记时设立。 第三百六十九条　居住权不得转让、继承。设立居住权的住宅不得出租，但是当事人另有约定的除外。 第三百七十条　居住权期限届满或者居住权人死亡的，居住权消灭。居住权消灭的，应当及时办理注销登记。 第三百七十一条　以遗嘱方式设立居住权的，参照适用本章的有关规定。",-1),c=[_,n];function l(i,d,p,h,m,f){return r(),t("div",null,c)}const x=a(s,[["render",l]]);export{b as __pageData,x as default};
