import{_ as a,c as t,o as r,m as e,a as o}from"./chunks/framework.D5hbrQnr.js";const b=JSON.parse('{"title":"第四章　产品责任","description":"","frontmatter":{},"headers":[],"relativePath":"repo/law-library/001 民法典/07 第七编：侵权责任/04 第四章：产品责任.md","filePath":"repo/law-library/001 民法典/07 第七编：侵权责任/04 第四章：产品责任.md"}'),s={name:"repo/law-library/001 民法典/07 第七编：侵权责任/04 第四章：产品责任.md"},_=e("h1",{id:"第四章-产品责任",tabindex:"-1"},[o("第四章　产品责任 "),e("a",{class:"header-anchor",href:"#第四章-产品责任","aria-label":'Permalink to "第四章　产品责任"'},"​")],-1),n=e("p",null,"第一千二百零二条　因产品存在缺陷造成他人损害的，生产者应当承担侵权责任。 第一千二百零三条　因产品存在缺陷造成他人损害的，被侵权人可以向产品的生产者请求赔偿，也可以向产品的销售者请求赔偿。 产品缺陷由生产者造成的，销售者赔偿后，有权向生产者追偿。因销售者的过错使产品存在缺陷的，生产者赔偿后，有权向销售者追偿。 第一千二百零四条　因运输者、仓储者等第三人的过错使产品存在缺陷，造成他人损害的，产品的生产者、销售者赔偿后，有权向第三人追偿。 第一千二百零五条　因产品缺陷危及他人人身、财产安全的，被侵权人有权请求生产者、销售者承担停止侵害、排除妨碍、消除危险等侵权责任。 第一千二百零六条　产品投入流通后发现存在缺陷的，生产者、销售者应当及时采取停止销售、警示、召回等补救措施；未及时采取补救措施或者补救措施不力造成损害扩大的，对扩大的损害也应当承担侵权责任。 依据前款规定采取召回措施的，生产者、销售者应当负担被侵权人因此支出的必要费用。 第一千二百零七条　明知产品存在缺陷仍然生产、销售，或者没有依据前条规定采取有效补救措施，造成他人死亡或者健康严重损害的，被侵权人有权请求相应的惩罚性赔偿。",-1),c=[_,n];function l(i,d,p,h,m,f){return r(),t("div",null,c)}const x=a(s,[["render",l]]);export{b as __pageData,x as default};
