import{_ as a,c as t,o as r,m as e,a as _}from"./chunks/framework.D5hbrQnr.js";const b=JSON.parse('{"title":"第九章　饲养动物损害责任","description":"","frontmatter":{},"headers":[],"relativePath":"repo/law-library/001 民法典/07 第七编：侵权责任/09 第九章：饲养动物损害责任.md","filePath":"repo/law-library/001 民法典/07 第七编：侵权责任/09 第九章：饲养动物损害责任.md"}'),o={name:"repo/law-library/001 民法典/07 第七编：侵权责任/09 第九章：饲养动物损害责任.md"},s=e("h1",{id:"第九章-饲养动物损害责任",tabindex:"-1"},[_("第九章　饲养动物损害责任 "),e("a",{class:"header-anchor",href:"#第九章-饲养动物损害责任","aria-label":'Permalink to "第九章　饲养动物损害责任"'},"​")],-1),n=e("p",null,"第一千二百四十五条　饲养的动物造成他人损害的，动物饲养人或者管理人应当承担侵权责任；但是，能够证明损害是因被侵权人故意或者重大过失造成的，可以不承担或者减轻责任。 第一千二百四十六条　违反管理规定，未对动物采取安全措施造成他人损害的，动物饲养人或者管理人应当承担侵权责任；但是，能够证明损害是因被侵权人故意造成的，可以减轻责任。 第一千二百四十七条　禁止饲养的烈性犬等危险动物造成他人损害的，动物饲养人或者管理人应当承担侵权责任。 第一千二百四十八条　动物园的动物造成他人损害的，动物园应当承担侵权责任；但是，能够证明尽到管理职责的，不承担侵权责任。 第一千二百四十九条　遗弃、逃逸的动物在遗弃、逃逸期间造成他人损害的，由动物原饲养人或者管理人承担侵权责任。 第一千二百五十条　因第三人的过错致使动物造成他人损害的，被侵权人可以向动物饲养人或者管理人请求赔偿，也可以向第三人请求赔偿。动物饲养人或者管理人赔偿后，有权向第三人追偿。 第一千二百五十一条　饲养动物应当遵守法律法规，尊重社会公德，不得妨碍他人生活。",-1),c=[s,n];function l(i,d,p,h,m,f){return r(),t("div",null,c)}const x=a(o,[["render",l]]);export{b as __pageData,x as default};
